import logo from './logo.svg';
import './App.css';

function App() {


async function connect() {
  const { ethereum } = window;
  provider = new ethers.providers.Web3Provider(ethereum);
  if (ethereum.isMetaMask) {
    const accounts = await provider.send("eth_requestAccounts", [])
    const { name, chainId } = await provider.getNetwork();
    setNetwork(name);
    setChainId(chainId);
    setPublickey(accounts[0]);
    setButtonText("Connected");
  } 

}


  return (
    <>

    <button className='wallet' onClick={connect}>Connect Wallet</button>

        <div class="container">
        <section class="section">
          <h2>UANS: Universal Asset Numbering Scheme</h2>
          <div class="input-group">
            <label>Chain Name:</label>
            <input type="text" id="chainName" />
          </div>
          <div class="input-group">
            <label>Chain ID:</label>
            <input type="text" id="chainId" />
          </div>
          <div class="input-group">
            <label>Contract Address:</label>
            <input type="text" id="contractAddress" />
          </div>
          <div class="input-group">
            <label>Token ID:</label>
            <input type="text" id="tokenId" />
          </div>
          <button onclick="generateOutputs()">Generate Outputs</button>
        </section>
        <section class="section output">
          <h2>Outputs</h2>
          <div id="outputContainer"></div>
        </section>
      </div>

    </>
  );
}

export default App;
